--
-- Create trigger [TRG_ISLEM_BASLIK_GUNCELLE] on table [dbo].[ISLEM_BASLIK]
--
GO
PRINT (N'Create trigger [TRG_ISLEM_BASLIK_GUNCELLE] on table [dbo].[ISLEM_BASLIK]')
GO
IF OBJECT_ID(N'dbo.TRG_ISLEM_BASLIK_GUNCELLE', 'TR') IS NULL
EXEC sp_executesql N'CREATE TRIGGER dbo.TRG_ISLEM_BASLIK_GUNCELLE
ON dbo.ISLEM_BASLIK
AFTER INSERT, UPDATE, DELETE
AS
BEGIN
    SET NOCOUNT ON;

    /* ================================
       1️⃣ CARI – ESKİ KAYITLARI GERİ AL
       ================================ */
    UPDATE C
    SET
        TOPLAM_GIREN = TOPLAM_GIREN - ISNULL(D.GIREN,0),
        TOPLAM_CIKAN = TOPLAM_CIKAN - ISNULL(D.CIKAN,0)
    FROM dbo.CARI C
    INNER JOIN deleted D ON D.CARIID = C.ID
    WHERE D.ISLEMTURU IN (1,5,6);

    /* ================================
       2️⃣ POS – ESKİ KAYITLARI GERİ AL
       (SADECE ODEMETURU = 2)
       ================================ */
    UPDATE P
    SET
        TOPLAM_GIREN = TOPLAM_GIREN - ISNULL(D.GIREN,0),
        TOPLAM_CIKAN = TOPLAM_CIKAN - ISNULL(D.CIKAN,0)
    FROM dbo.POS P
    INNER JOIN deleted D ON D.POSID = P.ID
    WHERE D.ODEMETURU = 2
      AND D.ISLEMTURU IN (1,5,6);

    /* ================================
       3️⃣ CARI – YENİ KAYITLARI EKLE
       ================================ */
    UPDATE C
    SET
        TOPLAM_GIREN = TOPLAM_GIREN + ISNULL(I.GIREN,0),
        TOPLAM_CIKAN = TOPLAM_CIKAN + ISNULL(I.CIKAN,0)
    FROM dbo.CARI C
    INNER JOIN inserted I ON I.CARIID = C.ID
    WHERE I.ISLEMTURU IN (1,5,6);

    /* ================================
       4️⃣ POS – YENİ KAYITLARI EKLE
       (SADECE ODEMETURU = 2)
       ================================ */
    UPDATE P
    SET
        TOPLAM_GIREN = TOPLAM_GIREN + ISNULL(I.GIREN,0),
        TOPLAM_CIKAN = TOPLAM_CIKAN + ISNULL(I.CIKAN,0)
    FROM dbo.POS P
    INNER JOIN inserted I ON I.POSID = P.ID
    WHERE I.ODEMETURU = 2
      AND I.ISLEMTURU IN (1,5,6);

END'
GO

