--
-- Create trigger [TRG_ISLEM_H_UPDATE_TOTALS] on table [dbo].[ISLEM_H]
--
GO
PRINT (N'Create trigger [TRG_ISLEM_H_UPDATE_TOTALS] on table [dbo].[ISLEM_H]')
GO
IF OBJECT_ID(N'dbo.TRG_ISLEM_H_UPDATE_TOTALS', 'TR') IS NULL
EXEC sp_executesql N'CREATE TRIGGER dbo.TRG_ISLEM_H_UPDATE_TOTALS
ON dbo.ISLEM_H
AFTER INSERT, UPDATE, DELETE
AS
BEGIN
    SET NOCOUNT ON;

    /* =====================================================
       STOK (ADET / MİKTAR)
    ===================================================== */
    ;WITH X AS (
        SELECT STOKID,
               SUM(GIREN) AS GIREN,
               SUM(CIKAN) AS CIKAN
        FROM (
            SELECT STOKID, GIREN, CIKAN
            FROM inserted
            WHERE ISLEMTURU IN (1,2,3)

            UNION ALL

            SELECT STOKID, -GIREN, -CIKAN
            FROM deleted
            WHERE ISLEMTURU IN (1,2,3)
        ) T
        GROUP BY STOKID
    )
    UPDATE S
    SET
        S.TOPLAM_GIREN += X.GIREN,
        S.TOPLAM_CIKAN += X.CIKAN
    FROM dbo.STOK S
    JOIN X ON X.STOKID = S.ID;
  

END'
GO

