--
-- Create procedure [dbo].[SP_REBUILD_ALL_TOTALS]
--
GO
PRINT (N'Create procedure [dbo].[SP_REBUILD_ALL_TOTALS]')
GO
IF OBJECT_ID(N'dbo.SP_REBUILD_ALL_TOTALS', 'P') IS NULL
EXEC sp_executesql N'CREATE PROCEDURE dbo.SP_REBUILD_ALL_TOTALS
AS
BEGIN
    SET NOCOUNT ON;
    SET XACT_ABORT ON;

    BEGIN TRAN;

    /* =====================================================
       1️⃣ CARI TOPLAMLARINI SIFIRLA
    ===================================================== */
    UPDATE dbo.CARI
    SET
        TOPLAM_GIREN = 0,
        TOPLAM_CIKAN = 0;

    /* =====================================================
       2️⃣ POS TOPLAMLARINI SIFIRLA
    ===================================================== */
    UPDATE dbo.POS
    SET
        TOPLAM_GIREN = 0,
        TOPLAM_CIKAN = 0;

    /* =====================================================
       3️⃣ STOK TOPLAMLARINI SIFIRLA
    ===================================================== */
    UPDATE dbo.STOK
    SET
        TOPLAM_GIREN = 0,
        TOPLAM_CIKAN = 0;

    /* =====================================================
       4️⃣ CARI – ISLEM_BASLIK’TAN HESAPLA
       ISLEMTURU:
       1,5 = GİRİŞ
       6   = ÇIKIŞ
    ===================================================== */
    ;WITH CARI_T AS (
        SELECT
            CARIID,
            SUM(GIREN) AS GIREN,
            SUM(CIKAN) AS CIKAN
        FROM dbo.ISLEM_BASLIK
        WHERE ISLEMTURU IN (1,5,6)
        GROUP BY CARIID
    )
    UPDATE C
    SET
        C.TOPLAM_GIREN = T.GIREN,
        C.TOPLAM_CIKAN = T.CIKAN
    FROM dbo.CARI C
    JOIN CARI_T T ON T.CARIID = C.ID;

    /* =====================================================
       5️⃣ POS – SADECE ODEMETURU = 2
    ===================================================== */
    ;WITH POS_T AS (
        SELECT
            POSID,
            SUM(GIREN) AS GIREN,
            SUM(CIKAN) AS CIKAN
        FROM dbo.ISLEM_BASLIK
        WHERE ODEMETURU = 2
          AND ISLEMTURU IN (1,5,6)
        GROUP BY POSID
    )
    UPDATE P
    SET
        P.TOPLAM_GIREN = T.GIREN,
        P.TOPLAM_CIKAN = T.CIKAN
    FROM dbo.POS P
    JOIN POS_T T ON T.POSID = P.ID;

    /* =====================================================
       6️⃣ STOK – ISLEM_H’TAN HESAPLA
       ISLEMTURU:
       1,2 = GİRİŞ
       3   = ÇIKIŞ
    ===================================================== */
    ;WITH STOK_T AS (
        SELECT
            STOKID,
            SUM(GIREN) AS GIREN,
            SUM(CIKAN) AS CIKAN
        FROM dbo.ISLEM_H
        WHERE ISLEMTURU IN (1,2,3)
        GROUP BY STOKID
    )
    UPDATE S
    SET
        S.TOPLAM_GIREN = T.GIREN,
        S.TOPLAM_CIKAN = T.CIKAN
    FROM dbo.STOK S
    JOIN STOK_T T ON T.STOKID = S.ID;

    COMMIT TRAN;
END'
GO

